package problems;

import java.util.HashMap;
import java.util.Map;

/**
 * @author rahulbhargava
 * @category 389. Find the Difference
 * @see https://leetcode.com/problems/find-the-difference/
 */

public class Solution_389 {
	public static void main(String[] args) {

		String s = "a", t = "aa";

		s = "";
		t = "";

		char result = spotDifferentVariable(s, t);
		System.err.println(result);

	}

	private static char spotDifferentVariable(String s, String t) {

		char[] sArr = s.toCharArray();
		char[] tArr = t.toCharArray();

		Map<Character, Integer> map = new HashMap<>();

		for (char element : sArr) {
			map.put(element, map.getOrDefault(element, 0) + 1);
		}

		for (char element : tArr) {
			Integer frequency = map.get(element);

			if (frequency == null) {
				return element;
			} else if (frequency > 1) {
				map.put(element, frequency - 1);
			} else if (frequency == 1) {
				map.remove(element);
			}
		}

		return ' ';
	}
}
